navigator.clipboard.readText().then((text) => {
  if (text) {
    try {
      new URL(text);
      openTrustedLinkIn(text, "tab");
    } catch (error) {
      const searchURL = `https://duckduckgo.com/?q=${encodeURIComponent(text)}`;
      openTrustedLinkIn(searchURL, "tab");
    }
  }
});

(async function() {
  const browserTransparent = Services.prefs.getBoolPref(
    "browser.tabs.allow_transparent_browser",
  );

  const prefs = [
    "browser.tabs.allow_transparent_browser",
    "mod.sameerasw.zen_transparent_glance_enabled",
    "widget.transparent-windows",
    "zen.widget.linux.transparency",
  ];

  for (let pref of prefs) {
    Services.prefs.setBoolPref(pref, !browserTransparent);
  }

  const zenInternetId = "{91aa3897-2634-4a8a-9092-279db23a7689}";
  const transparentZenId = "642854b5-88b4-4c40-b256-e035532109df";
  const zenInternet = await AddonManager.getAddonByID(zenInternetId);

  const utils = ChromeUtils.importESModule(
    "chrome://userscripts/content/engine/core/utils.mjs",
  ).default;
  const installedMods = await utils.getMods();
  const transparentZen = installedMods[transparentZenId];
  if (transparentZen) {
    if (
      (browserTransparent && transparentZen.enabled) ||
      (!browserTransparent && !transparentZen.enabled)
    ) {
      SineAPI.manager.toggleTheme(installedMods, transparentZenId);
    }
  }

  if (browserTransparent) zenInternet?.disable();
  else zenInternet?.enable();

  try {
    const showToast = ChromeUtils.importESModule(
      "chrome://sine/content/zen-custom-js/utils/toast.js",
    ).showToast;
    showToast({
      title: `Transparency ${browserTransparent ? "Disabled" : "Enabled"}`,
      description: "A restart is required for clhanges to take effect",
      preset: 1,
    });
  } catch {
    //ignore
  }
})();
